void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = iTime * 2.0;
    float zoom = 50.0;
    float clarity = 1.0;
    vec4 color_A = vec4(0, 0.60, 0.60, 1);
    vec4 color_B = vec4(0, 0.75, 0.75, 1);

    vec2 uv = fragCoord / iResolution.xy;

    float rad = radians(angle);
    vec2 direction = vec2(cos(angle) * uv.x, sin(angle) * uv.y);

    float position = direction.x + direction.y;
    float wave = sin(position * zoom) * clarity;

    fragColor = mix(color_A, color_B, clamp(wave, -1.0, 1.0));
}
