const float pi = 3.141592653589;
float mmod(float a, float b) { return a - floor(a/b); }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 c1 = vec2(0.5,0.5);
    float n = 0.0 + 1.0*sin(pi*mmod(iTime,1.0));
    float a = 10.0*mod(uv.x,0.1);
    float b = 10.0*mod(uv.y,0.1);
    vec3 col = vec3(0,1,0) * ( cos(2.0 * pi * (a+n)) * sin(2.0 * pi * (b+n)) );

    fragColor = vec4(col,1.0);
}
