#define Q T=pow(clamp((mod(iTime,3.)-S++),0.,1.),3.)
void mainImage(out vec4 O,vec2 C)
{
    O-=O;
	for(float T,S,i=9.;i>4.;i--){
        vec3 p=vec3(0,cos(iTime),i),
        d=vec3(C.xy/iResolution.y-.7,1.);
        S=0.;
        Q;
        vec2 u=abs((p+d*p.z/d.z).xy*mat2(cos(T*3.+vec4(0,2,5,0))))-T;
        O+=step(abs(length(u-clamp(u,-.6,.6))-.1),.05)*.8;
    }
}

