void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 newuv = vec2(uv.x + (uv.x + 100.*sin(iTime*2.)/200.),uv.y + (uv.y + 100.*cos(iTime*2.)/200.));

    vec4 finalcolor = vec4(0.);

    vec4 vandamm = texture(iChannel0,uv);
    vec4 br = texture(iChannel1,newuv);

    finalcolor = vandamm;



    if(finalcolor.g>0.6 && finalcolor.r<0.6){
        finalcolor = br;
    }

    finalcolor = vec4(vec3(finalcolor.b*0.07 + finalcolor.g*0.72 + finalcolor.r*0.21) ,1.);

    fragColor = finalcolor;

    //fragColor = vec4(newuv.x);
}
