#define OR(a,b) min(a,b)
#define NOT(a,b) max(a,-b)
#define NOT1(x) (-(x))
#define AND(a,b) max(a,b)
#define XOR(a,b) AND(OR(a,b), NOT1(AND(a,b)))

#define S(x) smoothstep(3./R.y,0.,x)

void mainImage(out vec4 O, in vec2 u) {
    vec2 R = iResolution.xy;
    vec3 p = vec3((u+u-R)/R.y,1);
    float t = 1000.;//p.x;

    for(int i = 0; i < 6; ++i) {
        vec3 p0 = vec3(fract(p.x),p.yz)*2.-vec3(1,0,0);
        float t0 = length( p0.xy )-1.;
        //t = XOR(AND(p.x/p.z,t0/p0.z),t);
        t = XOR(t,t0/p0.z);
        p = p*2.-vec3(1,0,0);
    }

    O = vec4(vec3(1,vec2(S(t))),1);
    O *= S(-(abs(t)-3./R.y));
}
