#define PI 3.14159
float pitch(float p)
{
    return pow(1.059460646483,p)*440.0;
}

vec2 mainSound( in int samp,float t)
{
    float mo=(sin(2.0*PI*2.0*t));
    float port=(sin(2.0*PI*220.0*t));
    return vec2(port*(0.3*mo));
}
