#define STEPS 60
#define E 0.001
#define PIXELR 0.5/iResolution.x
#define FAR 40.0
#define gamma vec3(2.2)

const vec3 AMBIENT = vec3(0.3, 0.3, 0.2);

const vec3 LC1 = vec3(0.3, 0.6, 0.8);
const vec3 LC2 = vec3(0.6, 0.4, 0.3);
const vec3 FOG = vec3(0.64, 0.62, 0.6);

struct Material{
    vec3 absorbed;

    vec3 lambertian;
    vec3 specular;
    float shininess;
    float sintensity;

    bool reflective;
    bool refractive;
    float reflectivity;
    float refractivity;
    int id;
};

Material getWaterMaterial(){
    Material mat;

    mat.absorbed = vec3(0.5, 0.6, 0.9);
    mat.lambertian = vec3(0.1, 0.4, 0.5);
    mat.specular = vec3(0.1, 0.19, 0.22);
    mat.shininess = 4.0;
    mat.sintensity = 0.2;

    mat.reflective = true;
    mat.refractive = true;
    mat.reflectivity = 0.5;
    mat.refractivity = 1.333;
    mat.id = 0;

    return mat;
}

Material getBlockMaterial(in vec2 id){
    vec2 idd = abs(id);
    vec2 s = smoothstep(vec2(0.4), vec2(0.8), idd);

    vec3 col = palette(length(id)*0.5,
                       vec3(0.2, 0.1, 0.25),
                       vec3(0.5, 0.5, 0.5),
                       vec3(1., 1., 0.),
                       vec3(0.25, 0.5, 0.8));

    Material mat;

    mat.absorbed = col;
    mat.lambertian = col;
    mat.specular = col*FOG;
    mat.shininess = 10.0;
    mat.sintensity = 0.5;

    mat.reflective = mod(s.x, 2.0) == 0.0;
    if(!mat.reflective){
        mat.reflectivity = 0.0;
        mat.reflective = false;
    }
    else{
        mat.reflectivity = 0.4;
        mat.reflective = true;
    }
    mat.refractive = true; 
    mat.refractivity = 1.52;

    if(mod(length(id), 6.0) == 0.0){
        mat.refractive = false;
		mat.reflectivity = 1.0;

    }

    mat.id = 1;

    return mat;
}

Material getGroundMaterial(){
    Material mat;

    mat.absorbed = vec3(0.4);
    mat.lambertian = vec3(0.5);
    mat.specular = vec3(0.3);
    mat.shininess = 80.0;
    mat.sintensity = 0.5;

    mat.reflective = false;
    mat.refractive = false;
    mat.reflectivity = 0.02;
    mat.refractivity = 3.6;
    mat.id = 3;

    return mat;
}


float scene(in vec3 p, out Material mat){

    float ground = fPlane(p, normalize(vec3(0.0, 1.0, 0.0)), 0.0);

    float pl = fPlane(p, normalize(vec3(0.0, 1.0, 0.0)), cos(iTime*0.15)-0.1);
    float h = noise(p*(sqrt(5.0)*0.5 + 0.3)*0.2+iTime*0.2);
    pl -= smoothstep(0.0, 1.0, h);;

    vec3 pp = p;
    vec2 n = vec2(3.0, 4.0);
    vec2 dif = n*0.5;
    vec2 id = pMod2(pp.xz, n);
    //id = abs(id);
    float idx = 1.0+sin(id.x);
    float y = abs(cos(idx)+sin(id.y))+0.5;

    float sp = fBox(pp-vec3(0.0, y, 0.0), vec3(1.0, y, 1.0));
    float guard = -fBoxCheap(pp-vec3(0.0, y, 0.0), vec3(n.x, 5.0, n.y)*0.5);
    guard = abs(guard) + n.x*0.1;

    if(pl < sp && pl < ground){

        mat = getWaterMaterial();
    }
    else if(ground < sp && ground < pl){
        mat = getGroundMaterial();
    }
    else{
        mat = getBlockMaterial(id);
   	}

    return min(ground, min(min(sp, guard), pl));
}

bool march(in vec3 o, in vec3 d, in float far, in bool inside, out vec3 p, out Material mat, out float t){
    t = E;
    float dir = inside ? -1.0 : 1.0;
    bool hit = false;
    for(int i = 0; i < STEPS/2; ++i){
        p = o + d*t;
        float dist = dir*scene(p, mat);

        if(abs(dist) < E || t > far){
            if(abs(dist) < E ){
            	hit = true;
            }
            break;
        }
        t += dist;
    }

    return hit;
}

//Enhanced sphere tracing algorithm introduced by Mercury
float marchP(in vec3 ro, in vec3 rd){
    float t = E;
    float step = 0.0;
    Material mat;

    float omega = 1.0;//muista testata eri arvoilla! [1,2]
    float prev_radius = 0.0;

    float candidate_t = t;
    float candidate_error = 1000.0;
    float sg = sgn(scene(ro, mat));

    vec3 p = vec3(0.0);

	for(int i = 0; i < STEPS; ++i){
		p = rd*t+ro;
		float sg_radius = sg*scene(p, mat);
		float radius = abs(sg_radius);
		step = sg_radius;
		bool fail = omega > 1. && (radius+prev_radius) < step;
		if(fail){
			step -= omega * step;
			omega = 1.;
		}
		else{
			step = sg_radius*omega;
		}
		prev_radius = radius;
		float error = radius/t;

		if(!fail && error < candidate_error){
			candidate_t = t;
			candidate_error = error;
		}

		if(!fail && error < PIXELR || t > FAR){
			break;
		}
		t += step;
	}
    //discontinuity reduction
    float er = candidate_error;
    for(int j = 0; j < 6; ++j){
        float radius = abs(sg*scene(p, mat));
        p += rd*(radius-er);
        t = length(p-ro);
        er = radius/t;

        if(er < candidate_error){
            candidate_t = t;
            candidate_error = er;
        }
    }
	if(t <= FAR || candidate_error <= PIXELR){
		t = candidate_t;
	}
	return t;
}


vec3 normal(in vec3 p){
    vec3 eps = vec3(E, 0.0, 0.0);
    Material mat;
    return normalize(vec3(
        scene(p+eps.xyy, mat) - scene(p-eps.xyy, mat),
        scene(p+eps.yxy, mat) - scene(p-eps.yxy, mat),
        scene(p+eps.yyx, mat) - scene(p-eps.yyx, mat)
    ));
}

//referenced from here
//https://blog.demofox.org/2017/01/09/raytracing-reflection-refraction-fresnel-total-internal-reflection-and-beers-law/
float schlick(float f0, float f1, float rr, vec3 n, vec3 d){
    float r = (f0-f1)/(f0+f1);
    r *= r;
    float angle = -dot(n, d);
    if(f0 > f1){
        float f = f0/f1;
        float sinT2 = f*f*(1.0-angle*angle);
        if(sinT2 > 1.0){
            return 1.0;
        }
        angle = sqrt(1.0-sinT2);
    }
    float x = 1.0-angle;
    r = r+(1.0-r)*pow(x, 5.0);
    return rr + (1.0-rr)*r;
}

vec3 phong(in vec3 n, in vec3 d, in vec3 ld, in Material mat){
    float lamb = max(dot(n,ld), 0.0);
    vec3 angle = reflect(n, ld);
    float spec = pow(max(dot(d, angle), 0.0), mat.shininess);

    return lamb*mat.lambertian*(1.0-mat.sintensity) + spec*mat.specular*mat.sintensity;
}

vec3 fog(in vec3 col, in vec3 p, in vec3 ro, in vec3 rd, in vec3 ld, in vec3 lc){
    float d = length(p-ro);
    float sa = max(dot(rd, -ld), 0.0);
    float fa = 1.0-exp(-d*0.06);
    vec3 fc = mix(FOG, lc, pow(sa, 4.0));
    return mix(col, fc, fa);
}

vec3 shadow(vec3 p, in vec3 ld){
    vec3 s = vec3(1.0);
    vec3 pr = p;
    Material mat;
    float t = 0.0;
    if(march(p, ld, 40.0, false, pr, mat, t)){
        s = vec3(0.1, 0.12, 0.15);
    }

    return s;
}

bool reflection(inout vec3 d, in vec3 n, inout vec3 p, out Material mat){
    d = reflect(d, n);
    vec3 pr = p;
    float t = 0.0;
    if(march(p+n*E*2.0, d, 40.0, false, pr, mat, t)){
        p = pr;
        return true;
    }
    return false;
}

bool refraction(inout vec3 d, in vec3 n, in float index, in bool inside, inout vec3 p, out Material mat){
    d = refract(d, n, index);
    vec3 pr = p;
    float t = 0.0;

    if(march(p-n*2.0*E, d, 20.0, inside, pr, mat, t)){
        p = pr;
        return true;
    }
}

bool primary(in vec3 o, in vec3 d, out Material mat, out vec3 p, out vec3 n){
    p = vec3(0.0);
    n = vec3(0.0);

    float t = marchP(o, d);
    p = o+d*t;
    float tt = scene(p, mat);
    if(t <= FAR){
        n = normal(p);
        return true;
    }


    /*if(march(o, d, 40.0, false, p, mat, t)){
        n = normal(p);
        return true;
    }*/



    return false;
}

vec3 shade(in vec3 rd, in vec3 p, in vec3 n, in vec3 ld, in Material mat){
    vec3 col = vec3(0.0);
    vec3 nr = n;
    vec3 pr = p;
    vec3 dir = rd;
    Material matr;

    col += phong(n, rd, ld, mat);
    float refractivity = 1.0; 
    float f = schlick(refractivity, mat.refractivity, mat.reflectivity, n, rd);


    if(mat.reflective){
        if(reflection(dir, n, pr, matr)){
            nr = normal(pr);
            col += phong(nr, dir, ld, matr)*f;
        }
    }
    dir = rd;
    pr = p;
    vec3 ppr = p;
    if(mat.refractive){

        if(refraction(dir, n, refractivity/mat.refractivity, true, pr, matr)){
            nr = normal(pr);
            col += phong(nr, dir, ld, matr)*(1.0-f);
        }
        if(refraction(dir, -nr, mat.refractivity, false, ppr, matr)){
            vec3 nnr = normal(ppr);
            float ff = schlick(mat.refractivity, refractivity, mat.reflectivity, -nr, dir);
            float d = distance(pr, ppr);
            vec3 absorbed = exp(-mat.absorbed * d);
            col += phong(nr, dir, ld, matr)*(1.0-ff);
            col *= absorbed;
        }
    }
    col *= shadow(p+n*4.0*E, ld);
    return col;
}

vec3 shade2(in vec3 rd, in vec3 p, in vec3 n, in vec3 ld, in Material mat){
    //primary
    vec3 col = phong(n, rd, ld, mat);

    //reflection rays

    vec3 colReflection = vec3(0.0);
    Material matReflection = mat;
    vec3 dReflection = rd;
    vec3 nReflection = n;
    vec3 pReflection = p;
    float f = 0.0; 
    for(int i = 0; i < 2; ++i){
        if(matReflection.reflective){
            if(reflection(dReflection, nReflection, pReflection, matReflection)){
                nReflection = normal(pReflection);
                f = schlick(1.0, matReflection.refractivity,
                            matReflection.reflectivity, nReflection, dReflection);
                colReflection += phong(nReflection, dReflection, ld, matReflection)*f;
            }
        }
    }
    colReflection /= 2.0;
    col += colReflection;

    vec3 colRefraction = vec3(0.0);
    Material matRefraction1 = mat;
    Material matRefraction2 = mat;
    vec3 dRefraction = rd;
    vec3 nRefraction = n;
    vec3 pRefraction1 = p;
    vec3 pRefraction2 = p;
    float refractivity = 1.0;
    float f1 = schlick(refractivity, mat.refractivity, mat.reflectivity, n, rd);
    float f2 = 0.0;
    //refraction rays
    for(int i = 0; i < 2; ++i){
        if(matRefraction1.refractive){
            //ray entering the object
            if(refraction(dRefraction, nRefraction,
                          refractivity/matRefraction1.refractivity, true, pRefraction1, matRefraction2)){
                nRefraction = normal(pRefraction1);
                colRefraction += phong(nRefraction, dRefraction, ld, matRefraction2)*(1.0-f1);

                f2 = schlick(matRefraction1.refractivity, refractivity, matRefraction1.reflectivity, -nRefraction, dRefraction);
            }
            //ray exiting the object
            if(refraction(dRefraction, -nRefraction, mat.refractivity, false, pRefraction2, matRefraction2)){
                nRefraction = normal(pRefraction2);
                float dist = distance(pRefraction1, pRefraction2);
                vec3 absorbed = exp(-mat.absorbed * dist);
                colRefraction += phong(nRefraction, dRefraction, ld, matRefraction2)*(1.0-f2);

                refractivity = matRefraction2.refractivity;
                pRefraction1 = pRefraction2;
                matRefraction1 = matRefraction2;
                f1 = schlick(refractivity, matRefraction2.refractivity, matRefraction2.reflectivity, nRefraction, dRefraction);
            }
        }
    }
    colRefraction /= 2.0;
    col += colRefraction;

    col *= shadow(p+n*2.0*E, ld);
    return col; 
}

vec3 shade3(in vec3 rd, in vec3 p, in vec3 n, in vec3 ld, in Material mat){
    //primary
    vec3 col = phong(n, rd, ld, mat);

    //calculating the reflection and refraction in same loop
    //the ray should really split in two after every reflection and refraction but
    //this is just fake way to do bit more bounces on both even though we get only
    //one path for each secondary ray.

    //reflection variables
    vec3 colReflection = vec3(0.0);
    Material matReflection = mat;
    vec3 dReflection = rd;
    vec3 nReflection = n;
    vec3 pReflection = p;
    float f = 0.0;

    //refraction variables
    vec3 colRefraction = vec3(0.0);
    Material matRefraction1 = mat;
    Material matRefraction2 = mat;
    vec3 dRefraction = rd;
    vec3 nRefraction = n;
    vec3 pRefraction1 = p;
    vec3 pRefraction2 = p;
    float refractivity = 1.0;
    float f1 = schlick(refractivity, mat.refractivity, mat.reflectivity, n, rd);
    float f2 = 0.0;

    //and here we go..
    for(int i = 0; i < 2; ++i){
        //reflection
        if(matReflection.reflective){
            if(reflection(dReflection, nReflection, pReflection, matReflection)){
                nReflection = normal(pReflection);
                f = schlick(1.0, matReflection.refractivity, matReflection.reflectivity,
                            nReflection, dReflection);
                colReflection += phong(nReflection, dReflection, ld, matReflection)*f;

            }

        }
        //refraction
        if(matRefraction1.refractive){
            //ray entering the object
            if(refraction(dRefraction, nRefraction,
                          refractivity/matRefraction1.refractivity, true, pRefraction1, matRefraction2)){
                nRefraction = normal(pRefraction1);
                colRefraction += phong(nRefraction, dRefraction, ld, matRefraction2)*(1.0-f1);

                f2 = schlick(matRefraction1.refractivity, refractivity, matRefraction1.reflectivity, -nRefraction, dRefraction);
            }
            //ray exiting the object
            if(refraction(dRefraction, -nRefraction, mat.refractivity, false, pRefraction2, matRefraction2)){
                nRefraction = normal(pRefraction2);
                float dist = distance(pRefraction1, pRefraction2);
                vec3 absorbed = exp(-mat.absorbed * dist);
                colRefraction += phong(nRefraction, dRefraction, ld, matRefraction2)*(1.0-f2);

                refractivity = matRefraction2.refractivity;
                pRefraction1 = pRefraction2;
                matRefraction1 = matRefraction2;
                f1 = schlick(refractivity, matRefraction2.refractivity, matRefraction2.reflectivity, nRefraction, dRefraction);
            }
        }
    }
    colReflection /= 2.0;
    col += colReflection;

    colRefraction /= 2.0;
    col += colRefraction;

    col *= shadow(p+n*2.0*E, ld);
    return col;
}

mat3 camera(in vec3 o, in vec3 t, in vec3 up){

    vec3 z = normalize(t-o);
    vec3 x = normalize(cross(z, up));
    vec3 y = normalize(cross(x, z));

    return mat3(x, y, z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = uv*2.0-1.0;//scaling from -1 to 1
    q.x *= (iResolution.x/iResolution.y);

    vec3 ro = vec3(3.0, 5.0, iTime*0.75);
    vec3 rt = vec3(8.0-10.0*smoothstep(0.0, 500.0, iTime), -3.0, ro.z+10.0);
    vec3 up = vec3(0.0, 1.0, 0.0);

    mat3 cam = camera(ro, rt, up);
    vec3 rd = normalize(cam*vec3(q, radians(60.0)));

    vec3 col = vec3(0.0);

    vec3 lp = vec3(20.0*sin(iTime*0.25)+ro.x, -10.0, 10.0*cos(iTime*0.25)+ro.z);
    vec3 lt = ro;
    vec3 ld = normalize(lt-lp);
    vec3 ld2 = normalize(ro-rt);

    Material mat;
    vec3 p = vec3(0.0);
    vec3 n = vec3(0.0);
    float t = 0.0;

    if(primary(ro, rd, mat, p, n)){
        //Light 1
        //col = shade2(rd, p, n, ld, mat);
        //light 2
        col += shade3(rd, p, n, ld2, mat);

        col += AMBIENT;
    }

    col = fog(col, p, ro, rd, ld, LC1);
    col = fog(col, p, ro, rd, ld2, LC2);

    col *= smoothstep(0.8, 0.1*0.799, distance(uv, vec2(0.5))*(0.6 + 0.1));

    col = smoothstep(0.08, 1.0, col);

    col = pow(col, 1.0/gamma);
    // Output to screen
    fragColor = vec4(col,1.0);

}

//Functions from different sources (not written by me)

//------------------------------------------------
//From HG sdf library: http://mercury.sexy/hg_sdf/
// Sign function that doesn't return 0
float sgn(float x) {
	return (x < 0.0)?-1.0:1.0;
}


float vmax(vec3 v) {
	return max(max(v.x, v.y), v.z);
}
// Box: correct distance to corners
float fBox(vec3 p, vec3 b) {
	vec3 d = abs(p) - b;
	return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}
// Cheap Box: distance to corners is overestimated
float fBoxCheap(vec3 p, vec3 b) { //cheap box
	return vmax(abs(p) - b);
}

// Plane with normal n (n is normalized) at some distance from the origin
float fPlane(vec3 p, vec3 n, float distanceFromOrigin) {
	return dot(p, n) + distanceFromOrigin;
}

// Repeat in two dimensions
vec2 pMod2(inout vec2 p, vec2 size) {
	vec2 c = floor((p + size*0.5)/size);
	p = mod(p + size*0.5,size) - size*0.5;
	return c;
}
//------------------------------------------------

// 3D noise function (IQ)
float noise(vec3 p){
	vec3 ip = floor(p);
    p -= ip;
    vec3 s = vec3(7.0,157.0,113.0);
    vec4 h = vec4(0.0, s.yz, s.y+s.z)+dot(ip, s);
    p = p*p*(3.0-2.0*p);
    h = mix(fract(sin(h)*43758.5), fract(sin(h+s.x)*43758.5), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z);
}

//Color palette function from IQ
//https://iquilezles.org/articles/palettes
vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d ){
    return a + b*cos( 6.28318*(c*t+d) );
}
