float fig(vec2 uv,float nb){
    return smoothstep(0.005,0.01,abs(length(uv)-0.3+sin(iTime+atan(uv.x,uv.y)*nb)*.15)-.02);
}
mat2 r(float a){
    float c=cos(a),s=sin(a);
    return mat2(c,-s,s,c);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    uv*=1.5;
    float d = fig(uv,4.0);
     float b = 0.;
    for(float i=0.;i<=1.;i+=1./4.){
        uv  = abs(uv)-.2;
        uv *= r(i*3.14);
        b +=fig(uv*r(i-iTime),3.0)*1./4.;
    }
      uv*=r(floor(length(sin(uv*10.)*.9)*10.));
     float q = fig(uv,10.0);
    vec3 col = vec3( (.5/d*b) + q ,(.3/d*b) + q  ,((sin(iTime+length(uv*10.))*.1+.2)/d*b) + q   );
    fragColor = vec4(col,1.0);
}
