void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec3 col = vec3(0.);
    uv.y = abs(uv.y-0.5);

    uv.x -= 0.5;
    uv.x = abs(uv.x);
    //uv.y = 1./(uv.y+0.25);
    uv.y = ((-uv.y*4.5)-0.5);
    uv.y += 0.5;
    uv.y *= .1;
    uv.y -= 0.5;
    uv.x *= 0.15;
    uv.x -= uv.x/(uv.y+0.5);
    uv.y += iTime*0.15;
    uv.y *= 0.35;

    col = vec3(mod(uv*10.,1.),0.);
    col = texture(iChannel0,col.rg).rgb;
    fragColor = vec4(col,1.0);
}
