// based on...
// https://www.shadertoy.com/view/lssfW2
highp float rand(vec2 co)
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    vec2 curve_p = vec2(0,0);
    float d = 10000.0;
    float di = 0.;
    for(int i = 0;i<500;i++)
    {
        di = float(i)*.03;
        curve_p.x = .3*sin(.4*iTime)+.15*float(di)*sin(float(di)+.2*iTime) +.01*float(di)*sin(4.0*di+.2*iTime) + .6*sin(iTime*.1)*float(di)*cos(11.0*di+.8*iTime);
        curve_p.y = .3*cos(.4*iTime)+.15*float(di)*cos(float(di)+.2*iTime) +.01*float(di)*cos(4.0*di+.2*iTime) + .6*sin(iTime*.1)*float(di)*sin(11.0*di+.8*iTime);
        d = min(d, length(p-curve_p)+.4*sin(iTime*.4)*rand(vec2(di,di)));

    }

    float r = .025*d*d*sin(.2*iTime);
    float g = .01*sin(.001*d*d+.1*length(p));
    float b = .25*abs(.15+.13*cos(iTime*.8 - 2.5*pow(d,2.0)));
    if (d>.05)
    {
        b=pow(b,2.25)*dot(p,vec2(4.,.5));
    } else
    {
        b = pow(b,.5)*d*2.*d;
    }
    if (d>.05 && d < .07)
    {
        r = pow(r,.5);
    }
    if (d>.07 && d < .09)
    {
        g = pow(g,.5);
    }
    vec3 col = vec3(r,g, b);
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    fragColor = vec4( pow(col,vec3(1./2.2)),1.0);
}
