void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;

    //vec2 p = .5*vec2(cos(iTime), sin(iTime));
    //uv += p;
	uv.x *= iResolution.x/iResolution.y;

    //uv *= pow(fract(4.*uv), vec2(.3));
    uv  *= 30.;
    vec3 col = vec3(0.);

    float d = length(uv);
    float r = 6.5;
    float D = r*r/d;
    vec2 new;
    float wop = 3.5;
    float smt = 1.;
    float smt1 = 2.;

    new.x = (uv.x - wop*cos(iTime))*D/d;
    new.y = (uv.y - wop*sin(iTime))*D/d;

    if(new.x < 0.) new.x -= smt*fract(smt1*iTime);
        else if (new.x > 0.) new.x += smt*fract(smt1*iTime);

    if(new.y < 0.) new.y -= smt*fract(smt1*iTime);
        else if (new.y > 0.) new.y += smt*fract(smt1*iTime);


    for(float i = -30.; i <= 30.; i++)
    {
        new += vec2(i, -i);
        for (float j = -30.; j <= 30.; j++)    
    		if (floor(new) == vec2(j))
        		col += vec3(1.4 - .85*pow(.13*(abs(i) + abs(j)), 3.), 1.2 - 1.2*pow(.14*(abs(i) + abs(j)), 3.), 0.)
                		* smoothstep(floor(new.x)-.03, floor(new.x) + .03, new.x)
                		* smoothstep(floor(new.y)-.03, floor(new.y) + .03, new.y);

        	else if (floor(new) == vec2(j-1., j))
                col += vec3(.38 - 1.2*pow(.13*(abs(i) + abs(j)), 3.), .7 -1.15*pow(.12*(abs(i) + abs(j)), 3.), .85 - 1.1*pow(.13*(abs(i) + abs(j)), 3.))
                	    * smoothstep(floor(new.x -1.) - .03, floor(new.x - 1.) + .03, new.x)
                		* smoothstep(floor(new.y) - .03, floor(new.y) + .03, new.y);

    		else col += vec3(0.);

        new -= vec2(i, -i);
    }




    fragColor = vec4(col,1.0);
}
