void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 Center = iResolution.xy / 2.0;
    float NSS = smoothstep(0.0, 1.0, (sin(iTime) + 1.0) / 2.0); // NormalizedSineSmoothstepped, NSS
    float HorizontalLine = iResolution.y * NSS;

    vec4 White = vec4(1.0);
  	vec4 Black = vec4(0.0);

    for(int i = 0; i < 30; i++)
    {
      if(length(gl_FragCoord.xy - Center) < (1000.0f / float(i) * 1.2) * (NSS + 0.5))
      {
        if(gl_FragCoord.y < HorizontalLine)
        {
        	i % 2 == 0 ? fragColor = White: fragColor = Black;
        }
        else
        {
            i % 2 == 0 ? fragColor = Black: fragColor = White;
        }
      }
  }
}
