void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float div = iResolution.x / iResolution.y;

    if (!(pow(uv.x - iMouse.x / iResolution.x, 2.) * div * div +
          pow(uv.y - iMouse.y / iResolution.y, 2.) < 0.2)) return;

   	vec3 col = 0.7 * vec3(cos(-iMouse.x / 10. + uv.x * 100. + iTime * 2.));
    col += 0.4 * vec3(sin(-iMouse.y / 10. + uv.y * 100.));

    col = vec3(col.y + sin(iTime), col.y - cos(iTime), col.z + sin(iTime));   

    // Output to screen
    fragColor = vec4(col,1.0);
}
