//Thaks to @FabriceNeyret2 for his tips
//Following wersion doesn't work on Safari and Edge (WebGL1)
void mainImage(out vec4 o, vec2 u)
{
  o=normalize(vec4(2e3,0,u+u-iResolution.xy));
  vec4 p;
  for(int i=0;++i<90;)
  	p=o*(o.y+=length(fract(vec3(length(p.zw),atan(p.z,p.w)*1.59+(p.x+=iTime)*.3,p))-.5)-.1);
  o/=p*abs(o);
}
