void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // Flip between these if you want zoom in over time at different rates, or just static zoom
    float zoomPower=(iTime*iTime);
    //float zoomPower = (iTime*5.0);
    //float zoomPower = iTime;
    //float zoomPower = 1.0;

    // Normalized mouse coords
    vec2 m = iMouse.xy/iResolution.xy/zoomPower;
    //float zoom = pow(10.0, -m.x*3.0);

    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    uv-=0.5;

    vec2 c = uv*1.0/zoomPower;
    c += m;
    c += vec2(-0.69955, 0.37999);

    vec2 z = vec2(0.0);
    float iter = 0.0;
    const float max_iter = 500.0;

    // Modify me for weird variations
    float weirdFlexButOk=2.0;

    for(float i=0.0; i<max_iter; i++){
        // Modify the below equation for weird effects
    	z = vec2(z.x*z.x - z.y*z.y, weirdFlexButOk*z.x*z.y) + c;
        if(length(z)>2.0) {break;}
        iter++;
    }

    float f = iter/max_iter;

    // Time varying pixel color
    vec3 col = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)))*f*2.0;
    // If you want static colour, comment out the above and uncomment below
    //vec3 col = vec3(f);

    // Output to screen
    fragColor = vec4(col,1.0);
}
