void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (2. * fragCoord - R) / R.y;
    vec2 pos = vec2(0.)-uv;
    float r = length(pos)*1.;
    float a = atan(pos.y,pos.x);
    float k = cos(a*min(9.,round(iTime))+iTime);
    float f = smoothstep(-.35,.9,k)*.2+.5;
    float pct = smoothstep(f+.02,f,r);
    pct -= smoothstep(f+.02,f,r*2.);
    vec3 col = vec3(pct);
    fragColor = vec4(col,1.);
}
