void mainImage( out vec4 O, vec2 U )
{
    O = T(U);
}

#define L1(z) ( Z = int(floor(float(z))), float( textureSize(iChannel1, Z).x >1) + (Z%2<1?.3:-.3) )
#define L2(z) ( Z = int(floor(float(z))), float( textureSize(iChannel2, Z).x >1) + (Z%2<1?.3:-.3) )
#define L3(z) ( Z = int(floor(float(z))), float( textureSize(iChannel3, Z).x >1) + (Z%2<1?.3:-.3) )

void mainImage( out vec4 O, vec2 U )
{
    O = T(U-vec2(1,0)); // scroll right

    float y = U.y/iResolution.y; int Z;

    if (U.x==.5)        // new measures in column 0
        if (y < .1 )
            O = vec4(iChannelResolution[1].x,0,0,0)  + .3;
        else if (y < .33 )
            O = vec4( L1(11.*(y-.1)/(.33-.1) ));
        else if (y < .43 )
            O = vec4(iChannelResolution[2].x,0,0,0)  + .3;
        else if (y < .67 )
            O = vec4( L2(11.*(y-.43)/(.67-.43) ));
        else if (y < .77 )
            O = vec4(iChannelResolution[3].x,0,0,0)  + .3;
        else 
            O = vec4( L3(11.*(y-.77)/(1.-.77) ));

}
