void mainImage(out vec4 O,in vec2 U)
{	
	vec3 u=vec3(-1.+2.*U/iResolution.xy,2);
    float t=0.;
    for(float i=0.;(i<=1.0);i+=.0125){	
        vec3 p=min(t,100.)*u;
        p=abs(fract(vec3(5.+4.*p.xy+2.*sin(iTime+p.y),cos(-p.z+iTime)+fract(1.57*p.y)+(6.*iTime+p.z))*.1)-.5);
        p.yz*=.25*(mat2(0,1,1,.45)*mat2(-1,-1,0,0));
        t+=1.-dot(p*p*7.,vec3(.5,2,0));
    }
    if((f(iTime)+fract(iTime))>1.)
         O.xyz+=2.*min(vec3(.5*u.zy,.5*f(iTime))*(0.05*t),1.-vec3(cos(2.*U.x)*sin(10.*iTime+U.y)));
    O.xyz-=.8*(O.y-O.x+O.z);
}

