#define g 1380.
#define bo 3. 
#define Brot(p, a) p = (sin(bo * a) + cos(g * a)) * p + sin(g * a) * vec2(p.y, -p.x)

float f(in float t)
{
    float tm=mod(t,2.);
    float m[]=float[](100.,280.,400.);
    float f=300.,s=0.;
    #define mC(t,p,sd)(sd+=sin(6.283185*p*t)*exp(-mod(t,2.)))
    if(mod(t,7.)>2.)((mod(t,8.)>4.)?tm:(tm*=.15));
    s=mC(tm,m[uint(2.*tm)%4u],s);
    mC(((mod(t,8.)<4.)?17.:7.)*t,f/.2,s);
    mC(tm,((mod(t,8.)<6.)?.0833:.5)*f,s);
    return mC(2.1*tm,s,s);
}
vec2 mainSound( in int samp,in float time)
{
    float tt = time;
    if(time<12.&&time>6.)tt=min(1.+mod(time,8.), mod(-time,.5));
    vec3 p=vec3(f(time)*.75,f(tt)*.95,f(time)*.5);
    float gt=mod(time,.125);
    float k=sin(870.*gt*exp(-6.*gt)*1.5)
        	*max(0.,1.-8.*gt)*float(int[](1,0,1,1,1,0,1)[uint(4.*time)%7u]);
    // return.707*(.8*k+max(p.xy,min(p.zx,p.xy)));
    Brot(p.xy, 0. );
    Brot(p.zy, 1.51 );
    Brot(p.zx, 3.11 );
    return.707*(.8*k+max(.5*k+p.yx,min(p.zx,p.yx)));
}
