precision mediump float;
#define WIDTH 0.061
//----------------реальный пример
    vec3 pal(in float t,in vec3 a,in vec3 b,in vec3 c,in vec3 d){
    return a+b*cos(3.14159*(c*t+d));
}
     vec3 spectrum(float n){
     return pal(n,vec3(.5,.5,.5),vec3(.5,.5,.5),vec3(1.0,0.7,0.4),vec3(0.00,0.15,0.20));
   }
//-----------------
    vec2 resize(vec2 st, vec4 rect){
    return vec2(rect[0], rect[1]) + vec2(rect[2], rect[3]) * st;
}

    float curve(float x){
    return fract(sin(x)*(100.0+iTime*.5));
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 st=fragCoord.xy/iResolution.xy;
    st = resize(st, vec4(-1.0, -.010, 2.0, 1.0));
    float bright = abs(st.y - curve(st.x)) < WIDTH ? 1.0 : 1.20;
    vec3 color=spectrum((bright*3.5-1.)*sin(.152*iTime)+1.4*st.y*st.x);//  вызов spectrum  vec3 color=vec3(0);
    color.zy*=st.y+sin(iTime*.1);//  сине-красный
    fragColor=vec4(color,1.);
}
