float Hash21(vec2 p)
{
	p = fract(p*vec2(234.34,435.345));
    p += dot(p,p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    //uv += iTime*0.05;    
    uv *= 4.;

    vec3 col = vec3(0);

    vec2 gv = fract(uv)-0.5;

    vec2 id = floor(uv);
    float n = Hash21(id);

	gv.x *= 2.0*step(0.5,n)-1.0;

	float width = 0.2;

    float d = abs(abs(gv.x+gv.y)-0.5);
	vec2 cUv = gv-sign(gv.x+gv.y+0.001)*0.5;
    d = length(cUv)-0.5;
    float mask = smoothstep(0.01,-0.01,abs(d)-width);

    float angle = atan(cUv.x,cUv.y);
    float checker = mod(id.x+id.y,2.);
    angle *= 2.*checker-1.;

    vec2 pUv = vec2(angle/1.57,(1./5.-d)*5.);
    pUv.x += iTime/2.;
    pUv.y = abs(pUv.y - 1.);
    pUv.x = pUv.x*4.;


    vec4 i = texture(iChannel0,pUv);
    vec3 img = vec3(i.x,i.y,i.z);

    col += mask*img;
    // Output to screen
    fragColor = vec4(col,1.0);
}
