void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 centerA = iResolution.xy/2.;
    vec2 centerB = centerA;
    centerA.x-=150.;
    centerB.x+=150.;
    float brightness=0.01;
    float deBrightA=pow(distance(fragCoord,centerA)*brightness,2.);
    float deBrightB=pow(distance(fragCoord,centerB)*brightness,2.);
    float brightA=sin(distance(fragCoord,centerA)/4.-iTime);
    float brightB=sin(distance(fragCoord,centerB)/4.-iTime);
    float pxVal = brightA/deBrightA+brightB/deBrightB;
    vec3 col = vec3(1.*pxVal);
    fragColor = vec4(col,1.0);
}
