float numWide = 20.0;
float numHeight = 4.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float resolutionRatio = iResolution.x/iResolution.y;
    float squareRatio = numWide/numHeight;

    //Correct for aspect ratio and ensure that boxes will be squares
    uv.x *= numWide * resolutionRatio;
    uv.y *= numHeight * squareRatio;

    //Interpolation values for vertical and horizontal
    float interpX = step(1.0, mod(uv.x, 2.0));
    float interpY = step(1.0, mod(uv.y, 2.0));

    //Create alternate textured squares    
    vec3 col = mix(texture(iChannel1, uv).rgb, texture(iChannel0, uv).rgb, mod(interpX + interpY, 2.0))
        * step(uv.x, floor(numWide * resolutionRatio));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
