float width_integer = 5.; 
float speed = 0.02;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float width= width_integer/iResolution.x;
    mediump vec2 uv = fragCoord.xy / iResolution.xy;
    float   pixel_dead = mod(iTime*speed,1. );
    float col_dead = -step(pixel_dead , uv.x  ) + step(pixel_dead ,uv.x +width );

    float i =  col_dead ;
    fragColor =vec4(uv,0.,1.)+ vec4(i,i,i,1.);
}
