#define t iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 col = vec3(0.);

    uv -= vec2(0.5);

   uv =  rot( sin(t) *3.14 ) * uv ;

    uv += vec2(0.5);



    float d = step(0.1, distance( uv, vec2(0.5) ) ) * step( distance( uv, vec2(0.5) ), 0.15 ) * step(uv.x, 0.5);  
    float d2 = step(0.3, distance( uv, vec2(0.5) ) ) * step( distance( uv, vec2(0.5) ), 0.35 ) * step(uv.y, 0.5);
    float d3 = step(0.4, distance( uv, vec2(0.5) ) ) * step( distance( uv, vec2(0.5) ), 0.45 ) *  step(0.5, uv.x);


    col.x += d;


    col.y = d2;
    col.z = d3;
    fragColor = vec4(col,1.0);
}
