void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0,uv);
}

vec4 tex(vec2 uv){
	return texture(iChannel0,uv/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    vec4 m = iMouse;
    fragColor = vec4(tex(uv));
    for(float i = 0.0; i<3.14/3.0; i+=0.01){
    fragColor += tex(uv+vec2(sin(i*100.0),cos(i*100.0))*i);
    }
    fragColor/=140.0;
    if(abs(m.x - uv.x) < 1.0 && abs(m.y - uv.y) < 1.0){
    	fragColor=vec4(10.0,0,0,1.0);
    }

    fragColor.x+=fragColor.z;
    fragColor.z += length(tex(uv)-tex(uv+vec2(1,0)))/8.0;
    fragColor.z += length(tex(uv)-tex(uv+vec2(-1,0)))/8.0;
    fragColor.z += length(tex(uv)-tex(uv+vec2(0,1)))/8.0;
    fragColor.z += length(tex(uv)-tex(uv+vec2(0,-1)))/8.0;
    fragColor.z += length(tex(uv)-tex(uv+vec2(1,1)))/8.0;
    fragColor.z += length(tex(uv)-tex(uv+vec2(-1,-1)))/8.0;
    fragColor.z += length(tex(uv)-tex(uv+vec2(-1,1)))/8.0;
    fragColor.z += length(tex(uv)-tex(uv+vec2(1,-1)))/8.0;
    fragColor.z /= 1.2;



    //fragColor=vec4(0.0,0.0,0.0,1.0);
}
