#define PI 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord /= iResolution.xy;
    vec4 Color0 = texture(iChannel0, fragCoord);
    vec4 Color1 = texture(iChannel1, fragCoord);
    //float t = clamp(iTime);
    float t = 0.5 - 0.5 * cos(2.0 * PI * iTime / 5.0);
    fragColor = mix(Color0, Color1, t);
}
