vec2 circlePos1 = vec2(0.3,0.5);
vec2 circlePos2 = vec2(0.5,0.5);
vec2 circlePos3 = vec2(0.6,0.3);
float radius1 = 0.15;
float radius2 = 0.15;
float radius3 = 0.05;
vec4 FG1 = vec4(0., 0., 1.0,1.0);
vec4 FG2 = vec4(0.129, 0.588, 0.952,1.0);
vec4 FG3 = vec4(0.090, 0.411, 0.6,1.0);
vec4 BG = vec4(0.,0.,0.2,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = fragCoord.xy / iResolution.xy;
 float aspectRatio = iResolution.y / iResolution.x;
 uv.y = uv.y * aspectRatio;
 // initierar musrörelse kopplat till canvasen
 vec2 move = iMouse.xy / iResolution.y;

 circlePos1.y = circlePos1.y * aspectRatio;
 circlePos2.y = circlePos2.y * aspectRatio;
 circlePos3.y = circlePos3.y * aspectRatio;

 // ändrar radien över tid för circle 1+2+3
 //radius1 -= sin(iTime) * 0.02;
 radius1 -= move.x * 0.05;
 //radius2 += sin(iTime) * 0.05;
 radius2 += move.x * 0.08;
 //radius3 += sin(iTime - 1.) * 0.025;
 radius3 += move.x * 0.03;
 //radius3 += sin(iTime + 5. / radius1) * 0.01;

 // ändrar färgerna på circel 1+2-3
 FG1 -= FG2.g * move.x;
 FG2 += FG1.b * move.x;
 FG3.r += sin(move.x - 1.) - FG1.r;
 //FG1 -= FG2.g * sin(iTime);
 //FG2 += FG1.b * sin(iTime);
 //FG3.r += sin(iTime - 1.) - FG1.r;
 FG1 *= uv.y;
 FG2 *= uv.y;
 FG3 *= uv.y * 2.;

 // ändra positionen över tid för circle 1+2+3
 //circlePos1 += sin(iTime) * 0.1; 
 //circlePos2 -= sin(iTime) * 0.1;
 //circlePos3 -= sin(iTime / 2.) * 0.1;


 fragColor = mix( FG2, BG, smoothstep(radius2 - 2e-3, radius2, length(circlePos2 - uv)));
 fragColor = mix( FG1, fragColor, smoothstep(radius1 - 2e-3, radius1, length(circlePos1 - uv)));
 fragColor = mix( FG3, fragColor, smoothstep(radius3 - 2e-3, radius3, length(circlePos3 - uv)));
}
