vec2 cMultiply( in vec2 a, in vec2 b ){
	return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x );
}
vec2 cInverse( in vec2 a ){
	return a*vec2(1.0, -1.0)/(length(a)*length(a));
}
vec2 cDivide( in vec2 a, in vec2 b ){
	return cMultiply( a, cInverse(b) );
}
vec2 cLog( in vec2 a ){
	return vec2( log(length(a)), atan(a.y, a.x) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Initialize values

    vec2 center = texture(iChannel0, vec2(0.0, 0.0)).xy;
    float zoom = pow(2.0, 1.0 - texture(iChannel0, vec2(0.0, 0.0)).z/5.0);

    vec2 z = vec2( 0.0, 0.0 );
    vec2 c = ( 2.0*fragCoord - iResolution.xy )*zoom/iResolution.y + center;



    // Run iterations

    float iter;
    float iter_limit = 10000.0;
    float bound = iter_limit;

    for( iter = 0.0; iter <= bound; iter++ )
    {
    	z = cMultiply(z, z) + c;
        bound = step( length(z), 2.0 )*iter_limit;
    }



    // Assign pixel

    float outside = step( iter, iter_limit );
    float inside = 1.0 - outside;
    float color = step( fract(iter/10.0), 0.49 );
    float shade = 1.0 - (length(z)-2.0)/8.0;
    fragColor = vec4( outside*color*shade, outside*color*shade, outside*shade + inside*0.5, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Initialize values

    vec2 center = texture(iChannel0, vec2(0.0, 0.0)).xy;

    float scroll = texture(iChannel0, vec2(0.0, 0.0)).z;
    float zoom = pow(2.0, 1.0 - scroll/5.0);

    float center_speed = 0.04;
    float scroll_speed = 0.06;



    // Apply center offset

    vec2 center_offset = vec2( 
        center_speed*( texture( iChannel1, vec2( 39.0/256.0, 0.0 ) ).x )
      - center_speed*( texture( iChannel1, vec2( 37.0/256.0, 0.0 ) ).x ), 
        center_speed*( texture( iChannel1, vec2( 38.0/256.0, 0.0 ) ).x )
      - center_speed*( texture( iChannel1, vec2( 40.0/256.0, 0.0 ) ).x ) );

    center_offset /= ( 1.0 - step( length(center_offset), center_speed ) )*(sqrt(2.0) - 1.0) + 1.0;
    center += center_offset*zoom*( texture( iChannel1, vec2( 32.0/256.0, 1.0 ) ).x + 1.0);



    // Apply scroll offset

    float scroll_offset = 
        scroll_speed*( texture( iChannel1, vec2( 87.0/256.0, 0.0 ) ).x )
      - scroll_speed*( texture( iChannel1, vec2( 83.0/256.0, 0.0 ) ).x );

    scroll += scroll_offset*( texture( iChannel1, vec2( 32.0/256.0, 1.0 ) ).x + 1.0);



	// Write to buffer

    fragColor = vec4( center, scroll, 1.0 );
}
