void mainImage(out vec4 color, vec2 coord)
{
    vec2 ps = 1.0 / iResolution.xy;
    vec2 uv = coord * ps;

    color = texture(iChannel0, uv);

    vec2 dir = Direction;

    if (iMouse.z > 0.0)
        dir = (0.5 - iMouse.xy * ps) * 2.0;

    vec4 glitch = SmudgeBlur(iChannel1, uv, dir * ps * Scale, Samples);
    color = max(color, glitch);
}

const vec2 Direction = vec2(0.0, 1.0);
const int Samples = 75;
const float Scale = 1.0;
const float Threshold = 0.775;
const vec3 ChromaKey = vec3(0.051, 0.639, 0.145);
const float ChromaKeyThreshold = 0.2;

vec4 SmudgeBlur(sampler2D sp, vec2 uv, vec2 dir, const int samples)
{
    if (samples < 1)
        return vec4(0);

    if (samples == 1)
        return texture(sp, uv);

    // If centering is desired...
    //uv -= dir * float(samples) * 0.5;

    vec4 color = texture(sp, uv);
    float scale = color.a;

    for (int i = 1; i < samples; ++i)
    {
        color = max(color, texture(sp, uv));
        uv += dir * scale;
    }

    //color /= float(samples);

    return color;
}

void mainImage(out vec4 color, vec2 coord)
{
    color = texture(iChannel0, coord / iResolution.xy);

    float key = distance(color.rgb, ChromaKey);
    key = smoothstep(ChromaKeyThreshold, 1.0 - ChromaKeyThreshold, key);
    key = clamp(key, 0.0, 1.0);

    color *= key;
}

float GetNoise(vec2 coord);

void mainImage(out vec4 color, vec2 coord)
{
    vec2 ps = 1.0 / iResolution.xy;
    vec2 uv = coord * ps;

    color = texture(iChannel0, uv);
    color.rgb *= GetNoise(coord);
    color.a = 1.0 - max(color.r, max(color.g, color.b));
}

float GetNoise(vec2 coord)
{
    vec2 uv = coord / 256.0;

    vec3 color = texture(iChannel1, uv).rgb;
    float noise = 1.0 - max(color.r, max(color.g, color.b));

    noise = step(Threshold, noise);

    return noise;
}
