#define pi 3.1415926
#define tau (2.0 * pi)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 offset = 0.1 * vec2(sin(uv.y * tau + iTime), cos(uv.x * tau + iTime));
    float f = pow(1.0 - texture(iChannel1, uv + offset).r, 3.0);
    f += pow(1.0 - texture(iChannel1, uv + iTime * 0.1).r, 3.0);
    fragColor = texture(iChannel0, uv) + f * 0.5;
    fragColor *= fragColor; // more contrast...
    //fragColor = smoothstep(.25, .9, texture(iChannel0, uv)) + f*.5; // ... or try the "Shane factor" :-)
}
