void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)*200./iResolution.y;


    vec3 o = vec3(0.,0.,.0);

    vec2 left = uv;
    left.x +=30.;

    vec2 right = uv;
    right.x -=30.;


    float lft_module = length(left);
    float rgt_module = length(right);

    vec3 unit = vec3(.6,.2,-.3);

    o = unit*sin(lft_module-iTime*12.)*3.+ 
        unit*sin(rgt_module-iTime*12.)*3.+1.;



    // Output to screen
    fragColor = vec4(o,1.0);
}
