#define PI 3.1415

float swirl(vec2 uv) {
    float angle = smoothstep(-1.0, -2.4 + pow(iTime * 0.5, iTime / 10.0), distance(vec2(0.0), uv)) * 4.0 * PI;
    return angle;
}

vec2 distort(vec2 uv, float fun) {

    mat2 mat = mat2(
    cos(fun), sin(fun),
    -sin(fun), cos(fun)
    );

    uv *= mat; 

    uv += 1.0;
    uv *= 0.5;

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 2.0;
    uv -= 1.0;

    float angle = smoothstep(-1.0, -2.4 + pow(iTime * 0.5, iTime / 10.0), distance(vec2(0.0), uv)) * 4.0 * PI;

    vec3 tex = texture(iChannel0, distort(uv, angle)).xyz;

    fragColor = vec4(tex,1.0);
}
