void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);

    float t = iTime + 0.;

    uv *= sin(t / 2.0) + 1.25;
    uv = abs(uv);

    float ang = 1.0;//(cos(t) + 1.0) * 2.0;
    uv *= mat2(cos(ang), sin(ang), -sin(ang), cos(ang)) * 0.7;	    

    uv = fract(uv);
    col += fract(uv.x + t) + fract(uv.y + t);

    vec3 rgb = sin(t + uv.xyx + vec3(0,2,4)) * 0.5 + 0.5;
    col *= rgb;

    col = pow(col, vec3(0.4545));

    fragColor = vec4(col,1.0);
}
