void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float aa = 1.5 / iResolution.y;
    vec3 col = vec3(.2);

    // fade
    col += mix(smoothstep(0.02+aa, 0.02, length(uv)), 0., smoothstep(0.0, 1., fract(iTime)));

    // position adjustment
    uv.x -= .5;
    uv.y -= .08;

    // triangle wave animation
    float scale = 1.;
    float mod = scale - abs(mod(abs(iTime), 2.*scale) - scale);

    // top circle
    float d = length(vec2(uv.x+mod, uv.y));
    float circle = smoothstep(0.02+aa, 0.02, d);
    col += circle;

    // 0->1 animation
    scale = 1.;
    mod = fract(iTime) * scale;

    // bottom circle
    d = length(vec2(uv.x+mod, uv.y+.16));
    circle = smoothstep(0.02+aa, 0.02, d);
    col += circle;

    fragColor = vec4(col,1.0);
}
