vec3 staticNoise (vec2 uv){
    vec3 col  ;
    //uv=mod(uv,1000.) ;
    for (int i=0;i<10;i++)
    {  
     uv.xy *=.998;
        uv.xy +=vec2(100.7*fract(uv.y),100.3*fract(uv.x));
        uv= vec2(uv.x*.7-uv.y*.6,uv.y*.53+uv.x*1.1);
col.zxy +=    vec3(   mod( (floor(uv.x*uv.x -uv.y*uv.y)+floor(uv.y*uv.y+uv.x*uv.x)),2.)   );

 }

   col=mod(col,2.);
      return col; 
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

 vec2 uv = fragCoord/iResolution.xy;
 vec3 col=staticNoise(uv  );

    fragColor = vec4(col,1.0);
}
