void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).rgb;
	col *= vec3(col.b+col.g);
    fragColor = vec4(col,1.0);
}

#define T(x,y) texture(iChannel0, tv+vec2(x,y)).rgb
//#define iTime (iTime)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = -1. + 2. * uv;
    vec2 R = iResolution.xy;
    vec2 tv = fragCoord/iResolution.xy;;
    vec2 px = vec2(1./R.x,1./R.y)*2.;
    vec3 c = T(0.,0.);
    px *= (1.-c.r)*(1.-c.r)*2.;
    vec2 fc = fragCoord.xy;
    vec3 col = vec3(0.);
    vec3 u = T(0,px.y);
    vec3 d = T(0.,-px.y);
    vec3 l = T(px.x,0.);
    vec3 r = T(-px.x,0.);

    vec3 udlr = (u+d+l+r)/4.;
    if (tv.x < .05 || tv.y < .05 || tv.x > 0.95 || tv.y > 0.95) {
        col.r = sin(uv.x+uv.y+iTime*0.1)*.5+0.5;
    }
    if (u.r > d.r || d.r > u.r) {
        col.b = ((u.r-d.r))*(20.);
    }
    if (l.r > r.r || l.r > r.r) {
        col.g = ((l.r-r.r))*(20.);
    }
    vec3 c2 = T((col.b)*px.x,(col.g)*px.y);

    col.r += c2.r*0.997;
    col.r *= 0.99;
    if (col.r > 1.) {
    	col.r = 1.;
    }

    //col.r = mix(col.r,c.r,0.1);
    fragColor = vec4(col, 1.);
}
