vec4 setColor(vec4 texColor,float red,float green,float blue)
{
       texColor.r*=red;
       texColor.g*=green;
       texColor.b*=blue;
       return texColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 xy = fragCoord/iResolution.xy;
     xy*=2.;

    vec4 texColor;
    texColor=texture(iChannel0,fract(xy));

    float l1=step(1.,xy.x)* step(1.,xy.y);
    float l2=step(1.,xy.x)* step(xy.y,1.);
    float l3=step(xy.x,1.)* step(xy.y,1.);
    float l4=step(xy.x,1.)* step(1.,xy.y);
     texColor=l1*setColor(texColor,0.,0.5,0.875)+
         l2*setColor(texColor,0.55,0.88,0.)+
         l3*setColor(texColor,0.5,0.5,0.5)+
         l4*setColor(texColor,0.99,0.,0.625);


    fragColor=texColor;

}
