float sdp(vec2 p, float r, float d){
    p = abs(p);
    float b = sqrt(r*r-d*d);
    return ((p.y-b)*d>p.x*b)?length(p-vec2(0.0,b)) : length(p-vec2(-d,0.0))-r;
}

float spiral(vec2 m) {
	float r = length(m), a = atan(m.y, m.x);
	return sin(75.*(sqrt(r) - .04 * a - .05 * iTime * 2.));
}

const vec3 bg = vec3(23., 32., 43.)/255.;
void mainImage(out vec4 O, in vec2 U){
    vec2 uv = (2. * U.xy - iResolution.xy)/iResolution.y * 1.3;
    float a = -iTime;
    uv *= mat2(cos(a), -sin(a), sin(a), cos(a));
    float l = length(uv),
          ma = mod(atan(uv.y, uv.x), acos(-1.)/3.5) + .95;
    vec2 muv = vec2(l * cos(ma), l * sin(ma));
    float v = .2 + smoothstep(.0, .1, abs(sdp(muv * 2. - vec2(0., .7), 1., .7)));
    v = min(v, .9 + smoothstep(.05, .6, abs(sdp(muv * 1., 1., 0.))));
    O = vec4(mix(bg, vec3(1.), step(v, spiral(uv))), 1.);
}
