//Circle function
float Circle(vec2 uv, vec2 pos, float r, float blur) {
    float d = length(uv-pos);
    float c = smoothstep(r, r-blur, d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

	/*
		Cirkeln c är vit inuti och svart utanpå..
		När vi multiplicerar denna med en färg, så
		ändras därför endast färgen i cirkeln (eftersom vit=1)
		och svart förblir svart (eftersom svart=0).
	*/
    float c = Circle(uv, vec2(.0), 0.4, 0.05);

    //lila bakgrund
    /*
		Vid addition så bevaras det vita eftersom värderna överstiger
		1, så det blir = 1. Svarta som är 0, adderas och blir lila.
	*/
    vec3 color = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = c * color;

    //Uppgift: create a smiling mouth.

    /*
		För att göra en mun, gör vi en svart cirkel, sedan en
		vit cirkel ovanpå denna. Sist så multiplicerar vi vår skapelse
		med col.
	*/
    float cB = Circle(uv, vec2(.0, -.01), 0.3, 0.015);
    float cW = Circle(uv, vec2(.0, .07), 0.3, -0.015);
    float c2 = cB * cW; //svart dominerar
    vec3 mouth = vec3(1,1,1) * c2;
    col -= c2;

    fragColor = vec4(col, 0);
}
