void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c = texture(iChannel0, uv);
    int y=int(c.r*255.0*255.0+c.b*255.0+c.g);
    if (y>0)
    switch (y%4)
    {
        case 0:
        fragColor = vec4(.1,.5,.5, 1.);
        break;
        case 1:
        fragColor = vec4(.3,.7,.3, 1.);
        break;
        case 2:
        fragColor = vec4(.7,.3,.3, 1.);
        break;
        case 3:
        fragColor = vec4(.3,.3,.7, 1.);
        break;
    }
    else
    fragColor = vec4(.0,.0,.0, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int n=0;
    if (iFrame==0)
    {
        n=((int(fragCoord.x)==int(iResolution.x/2.0))&&(int(fragCoord.y)==int(iResolution.y/2.0)))?1:0;
        fragColor = vec4(n*0,n*255,n*255,1.0);
    }
    else
    {
        vec4 gs=texelFetch(iChannel0,ivec2(int(fragCoord.x+iResolution.x)%int(iResolution.x),int(fragCoord.y+iResolution.y)%int(iResolution.y)),0);
        int sn=int(gs.r*255.0*255.0+gs.g*255.0+gs.b);
        sn=sn%4;
        vec4 g=texelFetch(iChannel0,ivec2(int(fragCoord.x+1.0+iResolution.x)%int(iResolution.x),int(fragCoord.y+iResolution.y)%int(iResolution.y)),0);
        n=int(g.r*255.0*255.0+g.g*255.0+g.b);
        sn+=n/4;
        g=texelFetch(iChannel0,ivec2(int(fragCoord.x-1.0+iResolution.x)%int(iResolution.x),int(fragCoord.y+iResolution.y)%int(iResolution.y)),0);
        n=int(g.r*255.0*255.0+g.g*255.0+g.b);
        sn+=n/4;
        g=texelFetch(iChannel0,ivec2(int(fragCoord.x+iResolution.x)%int(iResolution.x),int(fragCoord.y+1.0+iResolution.y)%int(iResolution.y)),0);
        n=int(g.r*255.0*255.0+g.g*255.0+g.b);
        sn+=n/4;
        g=texelFetch(iChannel0,ivec2(int(fragCoord.x+iResolution.x)%int(iResolution.x),int(fragCoord.y-1.0+iResolution.y)%int(iResolution.y)),0);
        n=int(g.r*255.0*255.0+g.g*255.0+g.b);
        sn+=n/4;
        fragColor = vec4(sn/255/255,sn/255%255,sn%255,1.0);
    }
}
