float Circle(vec2 uv, vec2 p, float r, float blur) {

    float d = length(uv-p); //distance    
    float c = smoothstep(r, r-blur, d);

    return c;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

	uv-= .5; // -0.5 <> -0.5				// to get to the center of the screen --- from x to y 
	uv.x *= iResolution.x/iResolution.y;    // to make a cirlce in a widescreen, making all sides equal

    vec3 col = vec3(0.); //color

    float mask = Circle(uv, vec2 (0.), .4, .05);


    mask -= Circle(uv, vec2 (-.13, .2), .07, .01);
    mask -= Circle(uv, vec2 (.13, .2), .07, .01);


    float mouth = Circle(uv, vec2 (0.), .3, .02); 
    mouth -= Circle(uv, vec2 (0., 0.1), .3, .02); 

    mask -= mouth;
   // col = vec3(mouth);

    // Output to screen
     col = vec3(1., 0., 1.) *mask; // purp (uv) circle

    fragColor = vec4(col, 1.0);
}
