#define PI 3.141592653
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime*0.5;
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    uv += vec2(sin(time*10.0)*0.1, cos(time*20.0)*0.1)*0.2;
    uv.y += 0.1;


    vec3 camRay = vec3(uv,1.0);

	vec3 pipeRay = camRay/length(camRay.xy);//*vec3(1.0, 1.0, 0.1);
    vec3 roadRay = camRay/-camRay.y*0.3;
    //pipeRay *= 1.0+0.5*sin((pipeRay.z+time)*5.0)/(pipeRay.z);

    vec3 combinedRay = pipeRay;
    if ((length(pipeRay) > length(roadRay)) && uv.y < 0.0) {
        combinedRay = roadRay;
    }

    /// Albedo:
    float cylindrical = atan(combinedRay.x, combinedRay.y);

    vec3 result = texture(iChannel0, 1.0*vec2(cylindrical/PI*2.0, combinedRay.z+time*.7)).rgb;
    float lines = distance(combinedRay,vec3(0.0, 0.0, combinedRay.z));
    lines = max(lines,mod(combinedRay.z+time*0.7, 1.0));
    lines = smoothstep(0.304, 0.301, lines);
    result = mix(result, vec3(1.0), lines);

    /// Lighting before fog:
    float light = 0.02;
    light += 0.1/(combinedRay.z);
    vec3 lampBob = vec3(sin(time*5.861)*0.04, sin(time*22.0)*0.02, 0.0);
    float spotlight = distance(combinedRay, lampBob + combinedRay.z*vec3(sin(time)*0.4, -0.05+0.2*sin(time), 1.0))/(combinedRay.z-0.5);
    spotlight = smoothstep(0.8, 0.2, spotlight);
    spotlight *= 1.5/pow(combinedRay.z, 2.0);
    light += spotlight;
    result *= light;

    /// Fog:
    result = mix(result, vec3(0.1,0.4,0.3)*0.5, clamp(combinedRay.z*0.2, 0.0, 0.9));

    result += spotlight*0.2;
    /// Lighting after fog:
    //result *= 1.0+0.2/(combinedRay.z*combinedRay.z);

    fragColor = vec4(result,1.0);
}
