#define time iTime
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}
vec3 sq(vec2 uv){
 vec2 ra =  vec2(0.1,0.1);
 float b = sdBox(uv,ra);
 float bump = abs(sin(iTime/3.)/50.);
 return  mix( vec3(0.) , vec3(1.0), 1.0-smoothstep(0.019+bump/2.,0.020+bump,abs(b)) );
}
vec3 scene(vec2 uv) {
uv = sin(uv*10.)/(10.+sin(time)*.10);;
return max(sq(uv * rotate2d(time)),sq(((uv* -rotate2d(time*3.))+vec2(0.1,0.1))* rotate2d(-time+log(time)))) ;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  (fragCoord-.5*iResolution.xy)/iResolution.y;

    float off = sin(time)*0.5+.5;
    vec3 color = mix(vec3(0.3,0.,0.32)+off,vec3(.5,.9,.9)-off,scene(uv));

    fragColor = vec4(
        color,
        1.0);
}
