#define scale 4.
float rnd(vec2 p)
{
 return fract(sin(dot(p, vec2(12.9898,78.233)))*43562.35344);
}

mat2 rot(float a)
{
 return mat2(cos(a), -sin(a), sin(a), cos(a));   
}
//unused checkerboard patter
float check(vec2 p)

{
    p*=8.;
    vec2 ind = vec2(floor(p.x), floor(p.y));
    float ch = mod(ind.x+ind.y, 2.0);
 return ch;   
}
vec2 rotCoord(vec2 p)
{
    float index = mod(floor(p.y*scale), 2.);
    p = fract(p*scale)-0.5;

    if (index< 1.)
    {
        p = p*rot(3.14159265359);
    }
    return p+0.5;
}

float shade(vec2 p)
{
return rnd(floor(p*scale));   

}
vec2 circle(vec2 p, float r)
{
    float ss = 0.01;
    float c = length(p);
    c = 1.0-smoothstep(r-ss, r+ss, c);
    float lh = smoothstep(-0.001, 0.001, p.x);
    float rh = step(p.x, 0.001);  
 return vec2(c*lh,c*rh);   
}

float rings (vec2 p)
{
 float ss = 0.01;
    float c = sin(length(p)*75.);
    //c = 1.0-smoothstep(r-ss, r+ss, c);
    //float lh = smoothstep(0., 0.01, p.x);
   /// float rh = step(p.x, 0.0);  
 return c;   
}

float vig (vec2 st)
{
    st *=  1.0 - st.yx;
    float vig = st.x*st.y*15.;
    vig = pow(vig, 0.1); 
    return vig;
}

//unused frame fuction
float frame(vec2 p)
{
 return step(-0.9, p.x)*step(p.x, 2.45)*step(-0.9, p.y)*step(p.y, 0.9); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  //Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
   vec2 st = uv;
       st.x*=iResolution.x/iResolution.y;
    st=st*2.0-1.0;

    float v = vig(uv);
    uv+=iTime/8.;
	float tx3 = vec3(texture(iChannel1, uv*4.)).x;
    float tx2 = vec3(texture(iChannel2, uv*4.)).x;
    uv.x*=iResolution.x/iResolution.y;

    float ch = check(st);

  //uv = fract(uv*4.);

    float sh = shade(uv);
    uv = rotCoord(uv);
    uv = uv*2.0-1.0;
    float tx1 = vec3(texture(iChannel0, uv)).x;


    float hf = smoothstep(-0.01, 0.01, uv.x);

    vec3 col = vec3(0.05);
    vec2 halves = circle(uv, 0.99);
    //col = mix(col, vec3(0.5, 0., 0.)*(1.-ch), 1.0-(halves.x+halves.y));



    col = mix(col, vec3(0.5-sh/5., 0., 0.), 1.-hf);

    //red half
    col = mix(col,vec3(0.8-sh/5., 0., 0.)-tx3/2., halves.x);

    //col = mix(col,vec3(0.2-sh/5.)*tx1, halves.y);
   vec2 newHalves = circle(uv, 0.32);
    //gold piece
    col = mix(col,vec3(212., 123., 15.)/255.*tx2*1.2, newHalves.x*step( 0.75, sh));

    //gold half
    col = mix(col,vec3(212., 123., 15.)/255.*tx2*2., halves.y+rings(uv)*halves.y*step(sh, 0.5));//*pow(tx2, 5.8));



	//col = mix(col, vec3(0.5, 0., 0.)*(1.-ch), 1.0-frame(st));
  //Output to screen
    fragColor = vec4(col*v,1.0);
}
