float vividLight(float a, float b)
{
    return b < 0.5
        ? 1.0 - ((1.0 - a) / (b * 2.0))
        : a / (1.0 - (b * 2.0 - 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float v = vividLight(uv.x, uv.y);
    fragColor = vec4(vec3(v), 1.0);
}
