// tentaclez

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float x = uv.x;
    float y = fract(uv.y * 2.0);

    vec3 col = vec3(x, y, 1.);

    float count = 3.;
    float xt = fragCoord.x / iResolution.x;
    float w = mix(1., 0., xt);
    float s1 = sin(x * 3.14*2. * count) * 0.1 * w + mix(0.4, 0.49, xt * 1.2);
    float s2 = sin(x * 3.14*2. * count) * 0.1 * w + mix(0.6, 0.51, xt * 1.2);

    if (y > s1 && y < s2)
        col = vec3(0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
