#define PI 3.14159265359
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.* (fragCoord.xy / iResolution.y - vec2(.8,.5));

	float r = length(uv); 
    float a = atan(uv.y,uv.x); // to polar
	float s1 = (a - 0.2 * 40.* 2.*PI*r + iTime);

	float c1 = smoothstep(-.25,.25,sin(s1)) * step(r, 0.5) ;
    float c2 = (1. - smoothstep(-.25,.25,sin(s1)) )* step(r, 0.5) ;
    vec4 color = vec4(hsb2rgb(vec3(r + 0.5, 1., 0.9)),1.) *c2  +vec4(hsb2rgb(vec3(r , 1., 0.9)),1.) *c1;

    //棍子
    float stick = 1. - smoothstep(-0.01, 0.01, abs(uv.x - 0.0 ) - 0.05);
    stick = stick * step(uv.y, -0.5);
    color += vec4(1.0, 0.0, 0.0, 1.) * stick;

	fragColor =  color;
}
