void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float v = texture(iChannel0, uv).x;

    fragColor = vec4(v, 0., 0., 1.);
}

vec2 read(vec2 coord) {
	return texture(iChannel0, coord / iResolution.xy).xy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if(iFrame <= 1) {
        fragColor = (fragCoord.x < 320. && fragCoord.y < 240.) ? vec4(1., 0., 0., 0.) : vec4(.0);
        return;
    }

    float right = read(fragCoord + vec2(1., 0.)).x;
    float left = read(fragCoord + vec2(-1., 0.)).x;
    float up = read(fragCoord + vec2(0., 1.)).x;
    float down = read(fragCoord + vec2(0., -1.)).x;
    vec2 cur = read(fragCoord);

    fragColor.y = cur.y + (right + left + up + down - 4. * cur.x) * iTimeDelta;

    fragColor.x = cur.x + fragColor.y * iTimeDelta;

    if(length(fragCoord - iMouse.zw) < 1.) {
    	fragColor.x += 1.;
    }
}
