renderpass.code:
    float step = 200.0;
    float amp = 1.0;
    float speed = 0.5;

vec3 hsb2rgb( in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

vec3 qrd(float x_, in float y_, in float step_, in float amp_, in float speed_) {

    float step = step_;
    float amp = amp_/step;
    float speed = speed_ * iTime * step;

    vec3 qrd = vec3(amp * (mod(pow(x_, 2.0) + pow(y_, 2.0) + speed, step_)), 0.0, 0.0);
    return qrd;
}

vec3 domainWarp(vec3 p, vec3 distort) {
    vec3 combined = p + distort;
    vec3 h = qrd(combined.x, combined.y, step, amp, speed);  
    vec3 combined2 = p + speed * h;
    vec3 domWarp = qrd(combined2.x, combined2.y, step, amp, speed);

    return domWarp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {    

    speed = (iMouse.x / iResolution.x);
    vec2 uv = fragCoord;//iResolution.xy;

    vec3 col = domainWarp(vec3(uv.x, uv.y, iTime), vec3(-4.0, -12.0, 0.0));


    fragColor = vec4(hsb2rgb(vec3(col.x, 1.0, 1.0)), 1.0);
}
